mapper.select:
select * from board
where title like #{searchWord}
order by
board_num desc limit #{startRow},#{pageBlock}

select * from board
where content like #{searchWord}
order by
board_num desc limit #{startRow},#{pageBlock}

update board set good =
good + 1
where board_num = #{board_num}

update board set report =
report + 1
where board_num = #{board_num}
